import React, { Component } from 'react';
import Suggestions from './suggestions'
import {debounce} from 'lodash';
var MagnifyingGlass = require('../assets/magnifying-glass.svg');
var cross = require('../assets/cross.svg');


class Search extends Component {
    state = {
      query: '',
      ships: []
    }  

    getInfo = (queryText) => {
        fetch('api/ships/'+queryText)
        .then(res => res.json())
          .then( (data ) => {
              console.log(data);
            this.setState({
             ships: data                             
            });
            console.log("i am caaling ships");
            console.log(this.state.ships);
          })
      }



    handleInputChange = debounce(() => {
      this.setState({
        query: this.search.value
      }, () => {
          if(this.state.query){
            this.getInfo(this.state.query);
          } else {
            this.setState({
                ships: []                             
               });
          }

      })
    }, 600);

    render() {
      return (
        <div className="container">
            <div className="row">
                <form>
                    <div className="col-xs-9 search">
                        <input className="form-control" type="search"
                        placeholder="Search for..."
                        ref={input => this.search = input}
                        onChange={this.handleInputChange}
                        />
                       <span className="col-xs-1 icon-search">
                            { this.state.query ? <img src={cross} height="30"/> : <img src={MagnifyingGlass} height="30"/> }
                        </span>

                    </div>
                </form>
            </div>
            <Suggestions ships={this.state.ships} />
        </div>
      )
    }
   }

export default Search
