import React, { Component } from 'react';
import Suggestions from './suggestions'

class Search extends Component {
    state = {
      query: '',
      ships: []
    }  

    getInfo = (queryText) => {
        fetch('api/ships/'+queryText)
        .then(res => res.json())
          .then( (data ) => {
              console.log(data);
            this.setState({
             ships: data                             
            });
            console.log("i am caaling ships");
            console.log(this.state.ships);
          })
      }



    handleInputChange = () => {
      this.setState({
        query: this.search.value
      }, () => {
          if(this.state.query){
            this.getInfo(this.state.query);
          } else {
            this.setState({
                ships: []                             
               });
          }

      })
    }

    render() {
      return (
        <div className="container">
            <div className="row">
                <form className="col-md-12">
                    <div className="form-group col-md-5">
                        <input className="form-control" type="search"
                        placeholder="Search for..."
                        ref={input => this.search = input}
                        onChange={this.handleInputChange}
                        />
                        <span className="input-group-append">
                            <div className="input-group-text bg-transparent"><img src="./assets/magnifying-glass.svg" height="30"/></div>
                        </span>
                    </div>
                </form>
            </div>
            <div className="row mx-md-n5">
            <Suggestions ships={this.state.ships} />
            </div>
        </div>
      )
    }
   }

export default Search
